class e extends HTMLElement{constructor(){super(),this.links=[{name:"Home",href:"home.html",active:this.checkPathname("index.html")},{name:"Films",href:"films.html",active:this.checkPathname("films.html")},{name:"Collections",href:"collections.html",active:this.checkPathname("collections.html")}]}checkPathname(e){return window.location.pathname.split("/")[1]===e}connectedCallback(){this.innerHTML=`\n      <header class="header">\n        <img class="header__logo" src="/logo.svg" height="60">\n        <nav class="header__nav">\n          <ul class="header__nav-list">\n            ${this.links.map((e=>`<li class="header__nav-item ${e.active?"header__nav-item--active":""}">\n                <a class="header__nav-link" href="${e.href}">${e.name}\n              </li>`)).join("")}\n          </ul>\n        </nav>\n      </header>\n    `}}customElements.define("header-component",e);class n extends HTMLElement{constructor(){super()}connectedCallback(){this.innerHTML='\n        <footer class="footer">\n          <div class="footer__nav-link">\n              <a href="https://www.coderscamp.edu.pl/" target="_blank">\n                <img class="footer__logo" src="/CodersCamp-Emblem.png" alt="coderscamp-logo">\n              </a>\n              <a href="https://github.com/mcwiekala/CodersCamp2021.Project.Web-Development-Basics" target="_blank">\n                <img class="footer__logo" src="/GitHub-Emblem.png" alt="github-logo">\n              </a>\n          </div>\n        </footer>\n      '}}customElements.define("footer-component",n);
